// Group bookmarks tagged with #consume by their topic tag
const bookmarks = $input.first().json.bookmarks || [];

console.log('Total bookmarks with #consume tag:', bookmarks.length);

if (bookmarks.length === 0) {
  return [];
}

// Group by topic tag (excluding 'consume' tag)
const tagGroups = {};
bookmarks.forEach(bookmark => {
  if (bookmark.tags && bookmark.tags.length > 0) {
    // Find the first tag that's not 'consume' (the topic tag)
    const topicTag = bookmark.tags.find(tag => tag.name !== 'consume');

    if (topicTag) {
      const tagName = topicTag.name;
      if (!tagGroups[tagName]) {
        tagGroups[tagName] = [];
      }
      tagGroups[tagName].push(bookmark);
    } else {
      console.log(`WARNING: Bookmark "${bookmark.title}" only has #consume tag, no topic tag`);
    }
  }
});

console.log('Topic tag groups:', Object.keys(tagGroups));

// Filter groups with 2+ bookmarks and create output
const result = [];
for (const [tag, items] of Object.entries(tagGroups)) {
  console.log(`Topic "${tag}" has ${items.length} bookmarks`);
  if (items.length >= 2) {
    result.push({
      tag: tag,
      bookmarkCount: items.length,
      bookmarks: items,
      currentDate: $input.first().json.currentDate
    });
  } else {
    console.log(`Skipping topic "${tag}" - only ${items.length} bookmark(s), need at least 2`);
  }
}

console.log('Final topic groups with 2+ bookmarks:', result.length);

return result.map(item => ({ json: item }));

// Build markdown content from bookmarks
const tag = $input.item.json.tag;
const bookmarks = $input.item.json.bookmarks;

// Format date as MM-DD-YYYY (consistent with tagging format)
const dateStr = $input.item.json.currentDate || new Date().toISOString().split('T')[0];
const dateObj = new Date(dateStr);
const month = String(dateObj.getMonth() + 1).padStart(2, '0');
const day = String(dateObj.getDate()).padStart(2, '0');
const year = dateObj.getFullYear();
const currentDate = `${month}-${day}-${year}`;

let markdown = `# Daily Digest - ${tag} - ${currentDate}\n\n`;

bookmarks.forEach((bookmark, index) => {
  markdown += `## Article ${index + 1}: ${bookmark.title || 'Untitled'}\n`;
  markdown += `Source: ${bookmark.url || 'No URL'}\n\n`;

  if (bookmark.content) {
    // Check if content is a string, if not convert it
    let contentText = typeof bookmark.content === 'string' 
      ? bookmark.content 
      : JSON.stringify(bookmark.content);

    // Limit content to first 500 characters to avoid huge podcasts
    const content = contentText.substring(0, 500);
    markdown += `${content}...\n\n`;
  } else if (bookmark.description) {
    markdown += `${bookmark.description}\n\n`;
  }

  markdown += `---\n\n`;
});

return {
  json: {
    tag: tag,
    currentDate: currentDate,
    episodeName: `Daily Digest - ${tag} - ${currentDate}`,
    markdownContent: markdown,
    bookmarks: bookmarks,
    bookmarkIds: bookmarks.map(b => b.id)
  }
};

// Pass episode metadata forward for Build Summary
const markdownNode = $('Build Markdown Content');
if (markdownNode && markdownNode.first()) {
  const data = markdownNode.first().json;
  return [{
    json: {
      episodeName: data.episodeName,
      tag: data.tag,
      bookmarkCount: data.bookmarkCount || (data.bookmarks ? data.bookmarks.length : 0)
    }
  }];
}
return [{ json: {} }];

// Build summary of all generated podcasts
const items = $input.all();
const podcasts = [];

items.forEach(item => {
  if (item.json.episodeName && item.json.bookmarkCount) {
    podcasts.push({
      topic: item.json.tag,
      articleCount: item.json.bookmarkCount,
      episodeName: item.json.episodeName
    });
  }
});

if (podcasts.length === 0) {
  return [{ json: { message: 'No podcasts generated today' } }];
}

const summary = podcasts.map(p => `${p.topic} (${p.articleCount} articles)`).join(', ');
const message = `ðŸ“» ${podcasts.length} podcast${podcasts.length > 1 ? 's' : ''} ready: ${summary}`;

return [{
  json: {
    message: message,
    podcasts: podcasts,
    count: podcasts.length
  }
}];

// Get episode details from Check Episode Status response
const statusData = $input.first().json;

console.log('Episode status data:', JSON.stringify(statusData, null, 2));

// The status check already returns the full episode data
// Extract what we need: episode_id and result.audio_file_path
const episodeId = statusData.episode_id || statusData.result?.episode_id;
const audioFile = statusData.result?.audio_file_path || statusData.audio_file;

if (!audioFile) {
  console.error('ERROR: No audio file found in episode data');
  console.error('Available fields:', Object.keys(statusData));
  if (statusData.result) {
    console.error('Result fields:', Object.keys(statusData.result));
  }
}

console.log('Episode ID:', episodeId);
console.log('Audio file path:', audioFile);

return [{
  json: {
    episode_id: episodeId,
    audio_file: audioFile,
    status: statusData.status
  }
}];

// Create show notes content from bookmarks
const markdownNode = $('Build Markdown Content');
if (!markdownNode || !markdownNode.first()) {
  console.log('ERROR: Could not find Build Markdown Content node');
  return [{ json: { showNotes: 'Error: Could not generate show notes' } }];
}

const markdownData = markdownNode.first().json;
const episodeName = markdownData.episodeName || 'Daily Digest';
const bookmarks = markdownData.bookmarks || [];

let showNotes = `${episodeName}\n\nArticles covered:\n`;

bookmarks.forEach((bookmark, index) => {
  showNotes += `\nâ€¢ ${bookmark.title || 'Untitled'}\n`;
  showNotes += `  ${bookmark.url || 'No URL'}\n`;
});

showNotes += `\n---\nAuto-generated from Karakeep bookmarks\n`;
showNotes += `Total articles: ${bookmarks.length} | Generated: ${markdownData.currentDate}\n`;

console.log('Show notes created:', showNotes.substring(0, 200));

return [{
  json: {
    showNotes: showNotes,
    episodeName: episodeName,
    audioFile: $input.first().json.audio_file
  }
}];

// Prepare data for copying files
const episodeName = $input.first().json.episodeName;
const audioFile = $input.first().json.audioFile;
const showNotes = $input.first().json.showNotes;

console.log('Episode name:', episodeName);
console.log('Audio file path in container:', audioFile);

// Base64 encode show notes to safely pass via SSH
// This handles newlines, special characters, and quotes
const showNotesB64 = Buffer.from(showNotes).toString('base64');

console.log('Show notes encoded (first 50 chars):', showNotesB64.substring(0, 50));

return [{
  json: {
    episodeName: episodeName,
    audioFile: audioFile,
    showNotesB64: showNotesB64,
    targetDir: '/Volumes/docker/container_configs/audiobookshelf/podcasts/Daily-Digests'
