// Get previous month (since we run on 1st to summarize last month)
const now = new Date();
const lastMonth = new Date(now.getFullYear(), now.getMonth() - 1, 1);
const year = lastMonth.getFullYear();
const yearShort = String(year).slice(-2);
const month = String(lastMonth.getMonth() + 1).padStart(2, '0');
const monthName = lastMonth.toLocaleString('default', { month: 'long' });

return [{
  json: {
    year: year,
    yearShort: yearShort,
    month: month,
    monthName: monthName,
    summaryFileName: `Summary-${month}-${yearShort}.md`
  }
}];

const data = $input.first().json;
const fileListOutput = data.stdout || '';
const monthData = $('Calculate Month Details').first().json;

// Parse file list
const files = fileListOutput.trim().split('\n').filter(f => f.length > 0);

if (files.length === 0) {
  return [{
    json: {
      error: true,
      message: `No weekly notes found for ${monthData.monthName} ${monthData.year}`,
      monthData: monthData
    }
  }];
}

return [{
  json: {
    error: false,
    files: files,
    fileCount: files.length,
    monthData: monthData
  }
}];

// Get AI Agent output
const aiOutput = $input.first().json;
const summary = aiOutput.output || "Summary generation failed";

// Get month data
const monthData = $('Calculate Month Details').first().json;
const fileCount = $('Parse Weekly Note Files').first().json.fileCount;

// Build the final markdown file
const finalContent = `${summary}\n\n---\n\n## Metadata\n\n- **Generated**: ${new Date().toISOString()}\n- **Source**: ${fileCount} weekly notes\n- **AI Model**: qwen2.5:7b via Ollama\n\n---\ntags: #monthlysummary #${monthData.year} #${monthData.monthName.toLowerCase()}`;

// Escape content for safe shell usage - use base64 encoding
const contentBase64 = Buffer.from(finalContent).toString('base64');

return [{
  json: {
    content: finalContent,
    contentBase64: contentBase64,
    fileName: monthData.summaryFileName,
    monthName: monthData.monthName,
    year: monthData.year,
    yearShort: monthData.yearShort,
    month: monthData.month
  }
}];
